 */
class MedTypeTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object MedTypeTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('MedType');
    }
    public function getTypes($id){
    	$q = Doctrine_Query::create() // CrÃ©ation de la requÃªte
        ->select('t.libelle, mt.id_medecin')
		->from('MedType mt')
		->leftJoin('mt.Type t') // On joint les deux tables.
		->where('id_medecin= ?', $id);
		return $q;
    }
}